// 汽車的抽象介面
public interface ICar {
    // 行駛汽車
    void run();
}

// 司機的抽象介面
public interface IDriver {
    // 司機駕駛汽車
    void drive();
}

// 實作司機的抽象類別
public class Driver implements IDriver {
    private ICar car;
    // 建構子方式注入抽象類別
    public Driver(ICar car){
        this.car = car;
    }    
    @Override
    public void drive() {
        car.run();
    }
}

