        {

            if (IsGpib)
            {
                return GpibCom.Write(cmd);
            }
            else
            {
                return UsbCom.Write(cmd);
            }

        }

        public int Read()
        {

            if (IsGpib)
            {
                return GpibCom.Read();
            }
            else
            {
                return UsbCom.Read();
            }

        }

    }

