public class ComFacotry
{
    public enum ComType { Gpib, Usb}

    public static IStrategy CreateCom(ComType type)
    {
        IStrategy com;

        switch (type)
        {
            case ComType.Gpib:
                com = new GpibCom();
                break;

            default:
                com = new UsbCom();
                break;

        }

        return com;
    }
}

class Program
{
     static void Main(string[] args)
    {
        var usbCom = ComFacotry.CreateCom(ComType.Usb);
        var cmd = usbCom.Write(Action);
    }
}
