public static void Main()
{
    Func<int, Func<int, int>> curriedMultiply = MultiplyCurried();
    Func<int, int> multiplyBy2 = curriedMultiply(2);

    int result = multiplyBy2(3); // result will be 6
    Console.WriteLine(result);
